export const currencyOptions = [
    { value: 'KRW', label: '대한민국 원 (₩)', format: (value) => `₩ ${Number(value).toLocaleString('ko-KR')}` },
    { value: 'USD', label: '미국 달러 ($)', format: (value) => `$ ${Number(value).toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}` },
    { value: 'EUR', label: '유로 (€)', format: (value) => `€ ${Number(value).toLocaleString('de-DE', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}` },
  ];
  
  export const formatCurrency = (value, currency) => {
    const option = currencyOptions.find(opt => opt.value === currency);
    return option ? option.format(value) : value.toString();
  };
