import React from 'react';
import { Typography, Grid, Card, CardContent, CardActions, Button, Box, AppBar, Toolbar, Link as MuiLink } from '@mui/material';
import { Link } from 'react-router-dom';

const tools = [
  { name: '연평균 수익률 계산기', icon: <TrendingUp />, link: '/cagr-calculator' },
  { name: '복리 계산기', icon: <PieChart />, link: '/compound-interest-calculator' },
  { name: '평단가 계산기', icon: <Calculator />, link: '/average-price-calculator' },
];

const MainPage = () => {
  return (
    <>
      <AppBar position="static" color="primary">
        <Toolbar sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', py: 1 }}>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            금융 계산기 by 직장인 조모씨
          </Typography>
          <MuiLink 
            href="https://minicode.tistory.com" 
            target="_blank" 
            rel="noopener noreferrer"
            sx={{ 
              color: 'white', 
              textDecoration: 'none',
              '&:hover': {
                textDecoration: 'underline',
              },
            }}
          >
            minicode.tistory.com
          </MuiLink>
        </Toolbar>
      </AppBar>
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Grid container spacing={4}>
          {tools.map((tool) => (
            <Grid item xs={12} sm={6} md={4} key={tool.name}>
              <Card>
                <CardContent>
                  {tool.icon}
                  <Typography variant="h6" component="div" sx={{ mt: 2 }}>
                    {tool.name}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" href={tool.link}>
                    사용하기
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </>
  );
};

export default MainPage;
