import React from 'react';
import { Typography, Grid, Card, CardContent, CardActions, Button, Box, Link as MuiLink } from '@mui/material';
import { Link } from 'react-router-dom';

const MainPage = () => {
  const tools = [
    { name: '연평균 수익률 계산기', path: '/cagr-calculator' },
    { name: '복리 계산기', path: '/compound-interest-calculator' },
    { name: '평단가 계산기', path: '/average-price-calculator' },
  ];

  return (
    <Box sx={{ maxWidth: 800, margin: 'auto', padding: 2 }}>
      <Typography variant="h4" gutterBottom align="center">
        금융 도구 모음
      </Typography>
      <Typography variant="h6" gutterBottom align="center">
        금융 계산기 by 직장인 조모씨
      </Typography>
      <Typography variant="body2" gutterBottom align="center" sx={{ mb: 4 }}>
        <MuiLink href="https://minicode.tistory.com" target="_blank" rel="noopener noreferrer">
          minicode.tistory.com
        </MuiLink>
      </Typography>
      <Grid container spacing={4}>
        {tools.map((tool) => (
          <Grid item xs={12} sm={6} md={4} key={tool.name}>
            <Card>
              <CardContent>
                <Typography variant="h6" component="div">
                  {tool.name}
                </Typography>
              </CardContent>
              <CardActions>
                <Button component={Link} to={tool.path} size="small" fullWidth>
                  사용하기
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default MainPage;
